import { useState } from "react";
import { addRecipe, updateRecipe } from "../services/api";

const useRecipeForm = () => {
  const [title, setTitle] = useState("");
  const [ingredients, setIngredients] = useState("");
  const [instructions, setInstructions] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState(null); // State to hold selected image file
  const [imageUrl, setImageUrl] = useState(""); // State to hold uploaded image URL

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const formData = new FormData();
      formData.append("title", title);
      formData.append("ingredients", ingredients);
      formData.append("instructions", instructions);
      formData.append("description", description);
      if (image) {
        formData.append("image", image); // Append the image file to FormData
      }

      const response = await addRecipe(formData); // Send FormData to API
      setImageUrl(response.imageUrl); // Update imageUrl state after successful upload
      clearForm();
    } catch (error) {
      console.error("Error adding recipe:", error);
    }
  };

  const handleEdit = async (recipeId) => {
    const formData = new FormData();
    formData.append("title", title);
    formData.append("ingredients", ingredients);
    formData.append("instructions", instructions);
    formData.append("description", description);
    formData.append("id", recipeId); // Assuming your backend can handle this

    if (image) {
      formData.append("image", image); // Append the image file to FormData if present
    }

    try {
      const response = await updateRecipe(recipeId, formData); // Send FormData to API
      setImageUrl(response.imageUrl); // Update imageUrl state after successful upload
      clearForm();
    } catch (error) {
      console.error("Error updating recipe:", error);
    }
  };

  const clearForm = () => {
    setTitle("");
    setIngredients("");
    setInstructions("");
    setDescription("");
    setImage(null);
    setImageUrl("");
  };

  return {
    title,
    ingredients,
    instructions,
    description,
    setTitle,
    setIngredients,
    setInstructions,
    setDescription,
    handleSubmit,
    handleEdit,
    image,
    setImage,
    imageUrl,
  };
};

export default useRecipeForm;
