import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import useRecipeForm from "../hooks/useRecipeForm";
import { getRecipeById, updateRecipe } from "../services/api";
import "../styles/components/_editRecipe.scss";

function EditRecipe() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [image, setImage] = useState(null);

  const {
    title,
    ingredients,
    instructions,
    description,
    setTitle,
    setIngredients,
    setInstructions,
    setDescription,
  } = useRecipeForm();

  useEffect(() => {
    const fetchRecipe = async () => {
      try {
        const recipe = await getRecipeById(id);
        setTitle(recipe.title);
        setIngredients(recipe.ingredients);
        setInstructions(recipe.instructions);
        setDescription(recipe.description);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching recipe:", error);
        setLoading(false);
      }
    };

    fetchRecipe();
  }, [id, setTitle, setIngredients, setInstructions, setDescription]);

  const handleImageChange = (event) => {
    setImage(event.target.files[0]);
  };

  const onSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("ingredients", ingredients);
    formData.append("instructions", instructions);
    formData.append("description", description);
    if (image) {
      formData.append("image", image);
    }
    await updateRecipe(id, formData);
    navigate("/recipes"); // Redirect to recipe list page after edit
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="edit-recipe-container">
      <h2>Edit Recipe</h2>
      <form onSubmit={onSubmit} className="edit-recipe-form">
        <input
          type="text"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="edit-recipe-input"
          placeholder="Title"
          required
        />
        <textarea
          value={ingredients}
          onChange={(e) => setIngredients(e.target.value)}
          className="edit-recipe-textarea"
          placeholder="Ingredients"
          required
        />
        <textarea
          value={instructions}
          onChange={(e) => setInstructions(e.target.value)}
          className="edit-recipe-textarea"
          placeholder="Instructions"
          required
        />
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="edit-recipe-textarea"
          placeholder="Description"
          required
        />
        <input
          type="file"
          onChange={handleImageChange}
          className="edit-recipe-file-input"
        />
        <button type="submit" className="edit-recipe-submit-btn">
          Update Recipe
        </button>
      </form>
    </div>
  );
}

export default EditRecipe;
