// src/hooks/useRecipeForm.js

import { useState } from "react";
import { addRecipe, updateRecipe } from "../services/api";

const useRecipeForm = () => {
  const [title, setTitle] = useState("");
  const [ingredients, setIngredients] = useState("");
  const [instructions, setInstructions] = useState("");
  const [description, setDescription] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();
    const recipe = { title, ingredients, instructions, description };
    try {
      await addRecipe(recipe);
      clearForm();
      // Optionally, you can handle success feedback or redirect to another page
    } catch (error) {
      console.error("Error adding recipe:", error);
    }
  };

  const handleEdit = async (recipeId) => {
    const recipe = {
      title,
      ingredients,
      instructions,
      description,
      id: recipeId,
    };
    try {
      await updateRecipe(recipe);
      clearForm();
      // Optionally, you can handle success feedback or redirect to another page
    } catch (error) {
      console.error("Error updating recipe:", error);
    }
  };

  const clearForm = () => {
    setTitle("");
    setIngredients("");
    setInstructions("");
    setDescription("");
  };

  return {
    title,
    ingredients,
    instructions,
    description,
    setTitle,
    setIngredients,
    setInstructions,
    setDescription,
    handleSubmit,
    handleEdit,
  };
};

export default useRecipeForm;
